<template>
  <div class="container">
    <div class="row">
      <div class="motto">
      </div>
    </div>
    <div class="row">
      <the-banner
        :dataset="banner.dataset"
        :config="banner.config"></the-banner>
    </div>
    <div class="row">
      <div class="item item__summary--horizontal">
        <div class="summary__thumbnail">
          <img align="left" src="../assets/img/gambar_isi.png">
        </div>
        <div class="summary__content">
          <div class="set__title content__title">
            <h2>Why Sharing?</h2>
          </div>
          <div class="content__description">
            <p class="highlight">Vue.js uses an HTML-based template syntax that allows you to declaratively bind the rendered DOM to the
              underlying Vue instance’s data. All Vue.js templates are valid HTML that can be parsed by spec-compliant
              browsers and HTML parsers. Under the hood, Vue compiles the templates into Virtual DOM render functions.
              Combined with the reactivity system, Vue is able to intelligently figure out the minimal number of
              components to re-render and apply the minimal amount of DOM manipulations when the app state changes.
              Vue.js uses an HTML-based template syntax that allows you to declaratively bind the rendered DOM to the
              underlying Vue instance’s data.</p>
          </div>
        </div>
      </div>
    </div>
    <div class="row" ref="carousel">
      <h2>Populer</h2>
      <the-carousel
        :dataset="carousel.top.dataset"
        :config="carousel.top.config"></the-carousel>
    </div>
    <div class="row section__content">
      <div class="category">
          <select class="input" name="Kategori" id="" v-model="filter.category">
            <option selected value>All</option>
            <option v-for="n in categories" :key="n.id" :value="n.id">{{n.categoryName}}</option>
          </select>
        </div>
      <div class="not-found__container" v-if="isShow.notFound">
        <div class="not-found__image">
          <i class="fas fa-paper-plane"></i>
        </div>
        <h2>Help Us Share Your Idea</h2>
        <p>Konten yang anda cari tidak ditemukan.</p>
      </div>
      <div class="grid-container" v-if="isShow.content">
        <div class="grid-item" v-for="n in latestContent" :key="n.id">
          <div class="item item__summary--vertical">
            <div class="summary__thumbnail">
              <img v-if="n.thumbnail" :src="'data:image/jpeg;base64,'+ n.thumbnail" :alt="n.thumbnail">
            </div>
            <div class="summary__label">
              <span class="label">{{ n.category }}</span>
            </div>
            <div class="summary__content">
              <div class="content__title">
                <h3> {{ n.title }} </h3>
              </div>
              <div class="content__description">
                <p>{{ n.content | truncate(36) }}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div style="text-align: center">
        <button v-if="isShow.button.loadMore" style="margin: 30px auto" class="button button--hollow" @click="load()">Load More</button>
      </div>
    </div>
  </div>
</template>

<script>
import axios from 'axios'
import Constant from '../constant'
import TheCarousel from '../components/TheCarousel'
import TheBanner from '../components/TheBanner'
export default {
  name: 'Home',
  components: {TheCarousel, TheBanner},
  data () {
    return {
      filter: {
        orderBy: '',
        direction: '',
        page: 1,
        size: 3,
        category: ''
      },
      isShow: {
        notFound: false,
        content: true,
        button: {
          loadMore: true
        }
      },
      latestContent: [],
      categories: [],
      carousel: {
        top: {
          config: {
            max: 0
          },
          dataset: {
            articles: [{
              title: '',
              content: '',
              thumbnail: ''
            }]
          }
        }
      },
      banner: {
        config: {},
        dataset: {
          contents: [{
            title: '',
            description: '',
            background: '',
            action: {
              text: '',
              link: ''
            },
            style: {
              align: '',
              mode: 0
            }
          }]
        }
      }
    }
  },
  mounted () {
    let self = this
    self.$nextTick(() => {
      self.init()
      console.log('hai', Constant.article.get)
    })
  },
  methods: {
    init () {
      let self = this
      self.getLatestArticle(3)
      axios.get(Constant.article.get, {
        params: {
          orderBy: 'viewer',
          direction: 'desc',
          size: 3,
          page: 1
        }
      })
        .then(response => {
          self.carousel.top.dataset.articles = response.data.content.data
        })
        .catch(error => {
          alert(error)
        })
      axios.get(Constant.lookup.category)
        .then(response => {
          self.categories = response.data.data
          console.log(self.categories)
        })
        .catch(error => {
          console.log(error)
        })
      self.banner.dataset.contents = [{
        title: 'Share Your Innovation',
        description: 'Bergabunglah bersama kami untuk...',
        background: '',
        action: {
          text: 'Upload',
          link: ''
        },
        style: {
          align: '',
          mode: 1
        }
      }, {
        title: 'Ceritakan Kisah Anda',
        description: 'Ceritakan isu-isu yang terjadi disekitarmu, masalah seperti apa yang dicari?',
        background: '',
        action: {
          text: 'Tulis Sekarang',
          link: ''
        },
        style: {
          align: '',
          mode: 0
        }
      }]
    },
    getLatestArticle (size) {
      let self = this
      axios.get(Constant.article.get, {
        params: {
          orderBy: 'id',
          direction: 'desc',
          size: size,
          page: 1,
          category: self.filter.category
        }
      })
        .then(response => {
          self.latestContent = response.data.content.data
          console.log(response.data.content.meta.totalPage)
          if (response.data.content.meta.totalPage === 1) {
            console.log('halo')
            self.isShow.button.loadMore = false
          }
        })
        .catch(error => {
          alert(error)
        })
    },
    load () {
      this.filter.size += 3
      this.getLatestArticle(this.filter.size)
    }
  },
  watch: {
    'filter.category': {
      handler () {
        this.getLatestArticle(3)
      },
      deep: true
    },
    latestContent (value) {
      console.log('hai')
      if (value.length === 0) {
        this.isShow.content = false
        this.isShow.notFound = true
        this.isShow.button.loadMore = false
      } else {
        this.isShow.content = true
        this.isShow.notFound = false
      }
    }
  }
}
</script>

